INSERT INTO `article` (`id`, `title`, `image`, `content`, `cat_id`, `author_id`, `status`, `view`, `created_at`) VALUES
(1, '۵ بازی مشابه Assassin’s Creed Valhalla که باید بازی کنید', '/Techblog/assets/upload/images/article/valhalla.jpg', 'آخرین بازی از فرنچایز اساسینز کرید یعنی بازی اساسینز کرید والهالا، یکی از آن بازی‌های گسترده و جهان بازی است که داستان وایکینگ‌ها را روایت می‌کند و جنبه‌های اکشن ادونچر (ماجراجویی) آن با تکیه بر المان‌های نقش‌آفرینی گیمرهای زیادی را به تحسین وا داشته است. در این بین باید اشاره کنیم که بسته الحاقی Dawn of Ragnarok هم مدتی است که عرضه شده و اگر طرفدار این بازی باشید به احتمال زیاد تا به الآن باید آن را تجربه کرده و چشم‌انتظار قسمت بعدی نشسته‌اید. پس با ویجیاتو و معرفی ۵ بازی مشابه Assassin’s Creed Valhalla همراه باشید.\r\n\r\nاز داستان‌سرایی مثال‌زدنی تا شخصیت پردازی‌ و طراحی و موسیقی‌های به کار رفته در Assassin’s Creed Valhalla همگی باعث می‌شوند تا این قسمت از مجموعه حرف‌های زیادی برای گفتن داشته باشد و هویت شکل گرفته تازه در میان این فرنچایز را به درجه بالایی برساند. یوبی‌سافت مونترال به واسطه طراحی و چینش درست مکانیزم‌های گیم‌پلی و سیستم کلی بازی سعی داشته تا این بار حس غوطه‌وری بیشتری را برای گیمر به ارمغان بیاورد و خوشبختانه اساسینز کرید والهالا حسابی به منبع ژانر خود وفادار است؛ مساله‌‎ای که بسیاری را ناخشنود و در عوض رضایت عده‌ای را به دست آورده است.', 1, 5, '1', 189, '2022-05-06 13:30:50'),
(2, 'بررسی لپ‌تاپ هواوی میت‌بوک D15 مدل ۲۰۲۱', '/Techblog/assets/upload/images/article/matebook.jpg', 'هواوی سری جدید لپ‌تاپ‌های اقتصادی میت‌بوک دی را وارد بازار کرده که اگرچه از نظر ظاهری شباهت زیادی با نسل قبلی دارند اما با پردازنده نسل یازدهم اینتل، عملکرد فوق‌العاده‌ای را ارائه می‌دهند. میت‌بوک D15 مدل ۲۰۲۱ اولین لپ‌تاپ از این خانواده است که می‌تواند یکی از بهترین اولترابوک‌های ۱۵ اینچی بازار لقب بگیرد. در این ویدیو ما به بررسی این لپ تاپ تازه‌ وارد پرداختیم و از ویژگی‌های مثبت و منفی آن برایتان گفتیم. همچنین به این سوال پاسخ دادیم که آیا هواوی میت‌بوک D15 مدل ۲۰۲۱ به درد تجربه بازی‌های ویدیویی می‌خورد یا نه؟ با ویجیاتو همراه باشید.', 2, 2, '1', 27, '2022-05-06 13:30:50'),
(3, 'Call of Duty: Mobile بیش از ۶۵۰ میلیون بار دانلود شده است', '/Techblog/assets/upload/images/article/20220514212832.webp', 'بازی Call of Duty: Mobile را می‌توان یکی از ویدیوگیم‌های موفق روی پلتفرم موبایل دانست که تابه‌امروز بازیکنان زیادی را به‌خود جذب کرده است و از زمان عرضه در سال ۲۰۱۹ تابه‌امروز، همچنان شاهد رشد میزان دانلود بازی هستیم. البته که بد نیست به محتویات بازی هم اشاره‌ای داشته باشیم. تیم سازنده سعی کرده است با ارائه‌ی محتویات مختلف، تجربه‌ی متنوع و تازه‌ای را دراختیار کاربران قرار دهد.\n\nحال در جریان گزارش مالی سالانه اکتیویژن (Activision)، ناشر سری کالاف دیوتی مشخص شده که به‌دنبال عرضه بازی کالاف دیوتی موبایل در چین، این بازی موبایل از زمان عرضه تا امروز در مجموع به بیش از ۶۵۰ میلیون بار دانلود دست پیدا کرده است. این در حالی است که در سال گذشته و همین بازه زمانی، میزان دانلود Call of Duty Mobile به بیش از ۵۰۰ میلیون بار رسید.\n\nدر ادامه مشخص شد که در جریان سال ۲۰۲۱ بازیکنان بیش از یک میلیارد دلار برای پرداخت‌های درون برنامه‌ای کال آو دیوتی موبایل هزینه کرده‌اند. ظاهرا تعداد بازیکنان فعال این بازی موبایل، تقریبا با تعداد بازیکنان فعال Call of Duty در پی سی و کنسول یکسان است.\n\nبااین‌تفاسیر نظر شما کاربران زومجی دراین‌باره چیست؟ آیا از تجربه بازی Call of Duty Mobile لذت می‌برید؟', 3, 1, '1', 55, '2022-05-14 21:28:32'),
(4, 'دنباله‌ی سریال بازی تاج و تخت با حضور جان اسنو ساخته می‌شود', '/Techblog/assets/upload/images/article/20220707214415.jpg', 'طبق گزارشاتی که اخیرا منتشر شده، شبکه‌ی HBO به تازگی مراحل ساخت دنباله‌ی سریال بازی تاج و تخت را که روی شخصیت جان اسنو تمرکز دارد، آغاز کرده است. همچنین کیت هرینگتون هم قرار است برای ایفای نقش شخصیتی که او را به این شهرت رساند، بازگردد.\r\n\r\nحدود سه سال از پایان یکی از محبوب‌ترین و پربیننده‌ترین سریال‌های تاریخ تلویزیون یعنی Game of Thrones می‌گذرد، سریالی اقتباسی از مجموعه رمان‌های ترانه یخ و آتش (A Song of Ice and Fire) نوشته‌ی جرج آر. آر. مارتین (George R. R. Martin)\r\nکه پخش آن از سال ۲۰۱۱ آغاز شد.\r\n\r\nسریال بازی تاج و تخت تا فصل چهارم خود بر اساس داستان رمان‌ها پیش رفت، اما از فصل پنجم سازندگان تصمیم گرفتند تا از داستان رمان‌ها جدا شوند و ادامه‌ی کار را طبق نظر خود پیش ببرند. فاصله‌ سریال و رمان در فصل‌های بعدی بیشتر شد و این دقیقا نقطه‌ای است که بسیاری از منتقدان و طرفداران معتقدند سریال با افت‌های همراه شد.\r\n\r\nسرانجام در سال ۲۰۱۹ پخش فصل هشتم سریال بازی تاج و تخت شروع شد که با بازخوردهای بدی همراه بود. اگرچه نمی‌توان گفت که همه از کیفیت فصل پایانی Game of Thrones ناراضی بودند، اما باید گفت تعداد معترضان به این فصل واقعا زیاد بود.\r\n\r\nحالا طبق گزارشی که توسط رسانه‌ The Hollywood Reporter منتشر شده، شبکه‌ی HBO در حال ساخت اسپین‌آف جدیدی در دنیای Game of Thrones است که در مراحل اولیه‌ تولید قرار دارد. این اسپین‌آف که قرار است دنباله‌ سریال بازی تاج و تخت باشد روی شخصیت جان اسنو تمرکز دارد و اولین سریالی خواهد بود که وقایع بعد از سریال اصلی را به ما نشان می‌دهد. همچنین کیت هرینگتون (Kit Harington) نیز دوباره به عنوان جان اسنو بازمی‌گردد.\r\n\r\nجان اسنو که در پایان سریال متوجه شد که وارث بالقوه‌ تخت آهنین است، آخرین بار در تبعید دیده شد، جایی که به مردمان آزاد (Free Folk) پیوست تا به سرزمین‌های آزاد در شمال دیوار برود. ادامه‌ داستان او یک حرکت مهم و البته پرریسک است. ساخت مجموعه‌ پیش‌درآمدی که به بررسی گذشته‌ وستروس می‌پردازد، نسبت به پیشبرد داستان شخصیت‌های محبوب بازی تاح و تخت با فشار کمتری همراه است و همچنین سازندگان می‌‌توانند از رمان‌ها راهنمایی زیادی بگیرند.\r\n\r\nبه جز این دنباله‌ی مورد انتظار، چندین اسپین‌آف دیگر نیز بر اساس سریال Game of Thrones در حال ساخت هستند که اولینشان سریال House of the Dragon است که ۲۰۰ سال پیش از وقایع سریال بازی تاج و تخت جریان دارد و پخش آن از تاریخ ۲۱ آگوست (۳۰ مرداد) آغاز خواهد شد. همچنین سه سریال پیش‌درآمد دیگر نیز که در وستروس جریان دارند، توسط HBO در حال تولید هستند. اولین سریال The Sea Snake است که روی یک دریانورد بزرگ به نام Corlys Velaryon تمرکز دارد و قرار است این شخصیت را در سریال House of the Dragon نیز ببینیم. دومین پیش‌درآمد Ten Thousand Ships است که ۱۰۰۰ سال پیش از وقایع بازی تاج و تخت اتفاق می‌افتند و داستان پرنسس Nymeria را روایت خواهد کرد که Dorne را تاسیس کرد. سومین پیش‌درآمد نیز بر اساس رمان Tales of Dunk and Egg که تنها ۹۰ سال قبل از Game of Thrones جریان خواهد داشت.\r\n\r\nعلاوه بر موارد فوق The Hollywood Reporter گزارش داده است که سه سریال انیمیشنی پیش‌درآمد نیز بر اساس این مجموعه در حال ساخت است که یکی از آن‌ها The Golden Empire است و روی سرزمین Yi Ti تمرکز دارد. البته باید گفت که احتمال لغو تولید هر یک از این آثار وجود دارد، مانند اولین اسپین‌آف بازی تاج و تخت با بازی نائومی واتس که قسمت پایلوت آن ساخته شد، اما نهایتا شبکه‌ی HBO تصمیم به لغو ساخت آن گرفت.', 1, 4, '1', 38, '2022-05-19 21:45:31'),
(5, 'سونی در حال کار بر روی دوال سنس جدید است', '/Techblog/assets/upload/images/article/20220707214730.jpg', 'تام هندرسن (Tom Henderson) افشاگر معروف دنیای ویدیو گیم مدعی شده سونی (Sony) در حال کار بر روی دوال سنس جدید است.\r\n\r\nدوال سنس (Dualsense) یا همان کنترلر‌های کنسول پلی استیشن 5 با ویژگی‌های جذابی از جمله دکمه‌های تریگر تطبیق پذیر معرفی شدند، این ویژگی بسیار جالب بود اما از طرفی به معنای پیچیدگی مکانیکی بیشتری در این کنترلر بود که همین امر باعث خرابی و اشکالات زیادی در این کنترلر می‌شد، مانند در رفتگی فنر زیر تریگر‌ها که باعث می‌شد این دکمه‌ها دیگر مثل قبل کار نکنند ( البته به راحتی قابل تعمیر بود) و البته مشکل دریفت (Drift) که خیلی سریع تر از آنچه که می‌بایست در این کنترلر رخ می‌داد. سونی مدتی بعد با عرضه نسل جدید دوال سنس که تغییرات فنی کمی کرده بود سعی در اصلاح این مشکلات کرد که تا حدی موفق بود. حال در جدید‌ترین شایعات منتشر شده توسط افشاگر معروف دنیای ویدیو گیم یعنی تام هندرسن، سونی در حال کار بر روی یک نسخه حرفه‌ای‌تر از کنترلر دوال سنس با نام پرو (Pro) است. مشابه این اقدام را قبلا مایکروسافت (Microsoft) با معرفی سری الیت (Elite) کنترلر‌های ایکس باکس کرده بود؛ که در این کنترلر‌ها ما شاهد دکمه‌های بیشتر با قابلیت شخصی سازی، تغییر میزان حساسیت تریگر‌ها و سری‌های متفاوت برای جوی استیک ( Joystick) ها بودیم.\r\n\r\nبر اساس گزارشاتی تازه سونی در حال کار بر روی کنترلر‌هایی جدید است که از آن‌ها با نام پرو یاد شده و احتمالا به زودی به صورت رسمی معرفی می‌شوند. این اخبار بر اساس گفته‌های افشاگر معروف دنیای ویدیو گیم، تام هندرسن نویسنده وبسایت Try Hard Guides، است که مدعی شده تصاویری از نمونه اولیه این کنترلر دیده، البته منبع این تصاویر خواسته تا ناشناس باقی بماند.\r\n\r\nهندرسن گفته است که پایه و اساس استایل این کنترلر همانند دوال سنس معمولی است و فقط در تعداد دکمه‌ها متفاوت ظاهر شده. همچنین این کنترلر دارای سری‌های مختلف برای جوی استیک است که با یک دکمه که در زیر جوی استیک قرار دارد قابل تعویض هستند، همچنین تریگر‌هایی با حساسیت قابل تنظیم، گریپ‌های قابل جداسازی و دکمه‌هایی در پشت کنترلر در دوال سنس جدید وجود دارند که هندرسن از این دکمه‌ها با عنوان «پدال‌های آویزان» یاد کرد؛ تقریبا همانند همان چیز‌هایی که در کنترلر الیت ایکس باکس دیدیم.\r\n\r\nهندرسن گفته است که منبع وی از دوال سنس جدید با عنوان «کنترلر پرو پلی استیشن 5» یاد کرده، هرچند که هنوز مشخص نیست که این نام رسمی آن خواهد بود یا خیر. البته به احتمال زیاد تا پایان ماه ژوئن شاهد رونمایی رسمی آن خواهیم بود.', 1, 5, '1', 19, '2022-05-19 21:58:54'),
(6, 'آیا دی ال سی Resident Evil Village قرار است سخت‌تر از بازی اصلی ', '/Techblog/assets/upload/images/article/20220707214954.jpg', 'بنا بر اعلام کپکام، دی ال سی بازی Resident Evil Village با نام Shadows of Rose بسیار دشوارتر از محتوای بازی اصلی خواهد بود.\r\n\r\nبرای بازی Resident Evil Village طی مراسم اخیر کپکام، محتواهای اضافه‌ جذاب و هیجان‌انگیزی معرفی شد. محتواهایی مثل اضافه شدن کارکترهای قابل بازی بیشتر به بخش مرسنریز و البته حالت سوم شخص باعث شد تا نگاه‌‌ها دوباره به سوی بازی Resident Evil Village جلب شود. علاوه بر این موارد قرار است دی ال سی Resident Evil Village با نام Shadows of Rose نیز عرضه شود.\r\n\r\nوقتی نسخه‌ Gold Edition بازی Resident Evil Village ارائه شد، اولین محتوایی که از آن معرفی کردند، دی ال سی Shadows of Rose بود. این دی ال سی داستانی پس از اتفاقات داستان اصلی بازی رخ می‌دهد و داستان رز وینترز، دختر ایتان وینترز را پیگیری می‌کند. همچنین اعلام شده که این دی ال سی داستانی قرار است بسیار سخت‌تر از بازی اصلی باشد.\r\n\r\nکاربری در توییتر با استناد به صفحه‌ توییتر رزیدنت اویل ادعا کرده که دی ال سی Resident Evil Village با نام Shadows of Rose درجه سختی بسیار بالاتری نسبت به بازی اصلی خواهد داشت و به دلیل کمبود آیتم‌هایی مثل مهمات و جان قرار است تجربه‌ به نسبت سخت‌تری را داشته باشیم. در دی ال سی Shadows of Rose که قرار است سخت‌تر از نسخه اصلی بازی باشد، روایت داستان متفاوت بوده و البته بازی در نقش رز، دختر ایتان وینترز جلو می‌رود در همین راستا دیگر ایتان وینترز پروتاگونیست این دی ال سی نیست.\r\n\r\nدر انتهای بازی Resident Evil Village مشخص شد که رز بزرگ شده و توسط کریس ردفیلد (Chris Redfield) تعلیم دیده است. البته به جز این موضوع هیچ اشاره‌ دیگری به کارکتر رز نشد و قرار است در دی ال سی Shadow Of Rose بیشتر در مورد او بفهمیم.\r\n\r\nدر حال حاضر شاید سخت باشد که حدس بزنیم کپکام چه برنامه‌ای برای رز دارد و او را قرار است به چه سمت و سویی ببرد. تنها مشخص است که رز قرار است به Village که زمانی تحت اسارت قرار گرفته بود، برگردد. همچنین به نظر می‌رسد که به احتمال زیاد به خاطر فاصله زمانی با داستان اصلی، کمپینی نسبتا متفاوت را شاهد باشیم. Resident Evil Village Gold Edition قرار است روز ۲۸ اکتبر (مصادف با ۶ آبان) برای پلتفرم‌های پلی استیشن ۵، پلی استیشن ۴، ایکس باکس سری اکس/اس، ایکس باکس وان، پی سی و گوگل استدیا منتشر شود.', 1, 4, '1', 52, '2022-05-20 21:58:54'),
(7, 'یوبیسافت ۱۵ سالگی فرنچایز Assassin’s Creed را با بازی رایگان جشن', '/Techblog/assets/upload/images/article/20220707215133.jpg', 'در وبسایت رسمی یوبیسافت (Ubisoft)، به نظر می‌رسد که شرکت سازنده فرنچایز Assassin’s Creed قصد دارد تا ۱۵ ساله شدن آن را جشن بگیرد! موردی که با یک رویداد رسمی برای برنده شدن اکشن فیگور ایوور (Eivor) از بازی Assassin’s Creed Valhalla انجام شده و Roadmap برنامه‌های بعدی مسابقه سری بازی اساسینز کرید را نشان می‌دهد.\r\n\r\nاز سال ۲۰۰۷ بود که ما اولین بار نام یک بازی به نام Assassin’s Creed را شنیدیم و با تجربه آن احساس فوق‌العاده متفاوتی را از داستان مهیج و گیم پلی بی‌نظیری که داشت به دست آوردیم! در واقع آن زمان بود که بسیاری از طرفداران سعی می‌کردند تا سبک پارکور داخل این بازی و بالا رفتن از دیوار را یاد بگیرند تا شبیه به کاراکترهای آن شوند. همچنین گذاشتن یک کلاه هودی روی سر که از قضا رنگش هم سفید بود، باعث میشد تا حس کنیم که ما هم یک اساسین هستیم و به فرقه برادران آن تعلق داریم!\r\n\r\nبه هر حال بازی Assassin’s Creed توانست سنگ بنای سری فوق‌العاده موفقی را بگذارد که امروزه هنوز هم جدیدترین عناوین آن (حتی با وجود تغییر نسبی سبک) توسط طرفداران استقبال می‌شود و از داستان‌های تاریخی و هیجان‌انگیز آن لذت می‌بریم. با این وجود پس از گذشت ۱۵ سال از عرضه اولین عنوان از این سری، شرکت یوبیسافت تصمیم گرفته است برای آن یک جشن برگذار کرده و طی آن بازی Assassin’s Creed Origin را از همین امروز رایگان هدیه دهد!\r\n\r\n\r\n\r\nچنان که در تصویر فوق می‌بینید، ظاهرا سازندگان قصد دارند تا کل تابستان سال جاری را صرف برگذاری یک مسابقه برای ارائه جوایز مختلف کنند. این مسابقه از همین امروز تا اواسط ماه سپتامبر که نزدیک پایان تابستان می‌شود، ادامه پیدا خواهد کرد و هر هفته روی یک بازی متمرکز خواهد بود؛ به نحوی که شما در یک مسابقه پرسش و پاسخ آنلاین شرکت کرده و باید سعی کنید تا پاسخ سوال‌ها را به صحیح‌ترین و سریع‌ترین شکل ممکن بدهید.\r\n\r\nاین در حالی است که اگر رتبه بهتری را نسبت به سایر افراد در جهان داشته باشید، شانس برنده شدن جوایز مهمی را خواهید داشت. جایزه‌هایی که برای این مسابقه در نظر گرفته شده، شامل یک اکشن فیگور از شخصیت ایوور و همچنین برخی آیتم‌های دیگر می‌شود که به قید قرعه به شرکت‌کنندگان داده می‌شود.\r\n\r\nهمچنین طی هفته‌های جاری احتمالا می‌توانید تک به تک بازی‌های سری را به رایگان دانلود و برای مدتی محدود تجربه کنید؛ به علاوه اینکه هم‌اکنون بازی Assassin’s Creed Origins برای کاربران دارنده اکانت در وبسایت یوبیسافت رایگان قرار داده شده است.', 1, 3, '1', 28, '2022-05-20 21:58:54'),
(8, 'آیا آیفون 7 به روزرسانی iOS 16 را دریافت می‌کند؟', '/Techblog/assets/upload/images/article/20220707215340.jpg', 'اپل هفته پیش در رویداد WWDC 2022 از نسخه جدید سیستم عامل آی او اس رونمایی کرد و سپس گوشی‌هایی را که این نسخه را دریافت می‌کنند اعلام کرد که ظاهرا آیفون 7 به روزرسانی iOS 16 را دریافت نمی‌کند.\r\n\r\nاپل پس از معرفی نسخه جدید سیستم عامل موبایلش، لیستی از گوشی‌هایی منتشر کرد که iOS 16 را دریافت می‌کنند اما وقتی نام آیفون 7 در این لیست مشاهده نشد، بسیاری از کاربران شوکه شدند.\r\n\r\nسال گذشته وقتی در جریان WWDC 2021 اعلام شد که آیفون 6 اس و 6 اس پلاس iOS 15 را دریافت خواهند کرد، همه‌ی کاربران هیجان زده شدند، زیرا همه انتظار داشتند iOS 14 آخرین به روزرسانی نرم افزاری باشد که آیفون 6 اس قرار است دریافت کند.\r\n\r\nاگرچه اپل خداحافظی آیفون 6 اس را به تعویق انداخت، اما اکنون بعد از یک سال، علاوه بر آیفون 6 اس و آیفون 6 اس پلاس آیفون 7 نیز به روزرسانی iOS 16 اپل را دریافت نخواهد کرد. \r\n\r\nبه نظر می‌رسد iPhone 6S طولانی‌ترین آپدیت نرم‌افزاری را از سوی اپل دریافت کرده است.\r\n\r\nدر این مقاله قصد داریم نگاهی به آینده پشتیبانی نرم افزاری اپل بیندازیم و ببینیم آیا این پشتیبانی کوتاه‌تر یا طولانی‌تر می‌شود. \r\nآیفون 6 اس: آیا طولانی‌ترین پشتیبانی نرم افزاری را دریافت کرد؟\r\n\r\nآیفون 6S از نظر فنی قرار نبود شش آپدیت iOS و هفت سال پشتیبانی نرم افزاری را دریافت کند. پشتیبانی نرم‌افزاری اپل فقط شامل پنج سال به‌روزرسانی سیستم‌عامل و در مجموع شش سال پشتیبانی مداوم از زمان عرضه آیفون ادامه دارد.\r\n\r\nاما این موضوع در مورد آیفون 6 اس و آیفون 6 اس پلاس طولانی‌تر شد چرا که این گوشی توانست از iOS 15 پشتیبانی کند و به این ترتیب آیفون 6 اس طولانی‌ترین گوشی پشتیبانی کننده از iOS در تاریخ محصولات اپل به شمار می‌رود.\r\n\r\nبه هر حال، نزدیک به هفت سال پس از عرضه اولیه آیفون 6 اس و 6 اس پلاس، این گوشی‌ها قابل استفاده هستند. \r\n\r\nمطمئنا پس از شش به‌روزرسانی سیستم‌عامل و هفت سال عمر، آیفون 6 اس کند می‌شود. اما به هیچ وجه کاربر را از برقراری تماس، ارسال پیامک، تماشای ویدیوهای یوتیوب و گشت و گذار در شبکه‌های اجتماعی بازنمی‌دارد،.\r\n\r\nبا این حال، این واقعیت که این آیفون قدیمی همان نسخه iOS را با آخرین و بهترین آیفون 13 پرو مکس اجرا می‌کند و اصول اولیه را پوشش می‌دهد، چیزی است که باید اپل را برای آن تحسین کرد!\r\n\r\nاما در این میان آیفون 7 همراه با آیفون 6 اس به روزرسانی iOS 16 را دریافت نکرد.', 1, 2, '1', 14, '2022-05-20 21:58:54'),
(9, 'سیستم مورد نیاز بازی Resident Evil 2 تغییر کرد', '/Techblog/assets/upload/images/article/20220707215623.jpg', 'در پی توسعه‌ی نسخه‌ی نسل جدید Resdient Evil 2 توسط کمپانی کپکام، مشخصات سیستم مورد نیاز بازی دستخوش تغییراتی شده است.\r\n\r\nمحبوبیت سری رزیدنت اویل برهیچکس پوشیده و پنهان نیست. فرنچایزی که تا به امروز نسخه‌های متعددی از آن توسط شرکت کپکام راهی بازار شده که اگرچه تقریبا هر یک از آن‌ها عملکرد فوق‌العاده‌ای از خود به نمایش گذاشته‌اند، اما حساب چندین بازی نخست مجموعه از دیگر عناوین آن کاملا جدا است. به همین خاطر، چند سالی است که کپکام در کنار ساخت دنباله‌ی رزیدنت ایول، بازی‌های قدیمی این سری را نیز بازسازی می‌کند.\r\n\r\nدر همین راستا، در سال ۲۰۱۹ میلادی بود که ریمیک رزیدنت ایول ۲ در دسترس بازیکنان قرار گرفت. این عنوان به لطف تلاش تیم سازنده در بازسازی کامل بازی حسابی مورد استقبال هواداران قرار گرفت و بازخوردهای مثبتی را از سوی منتقدها دریافت کرد. در همین حین، نوبت به ریمیک نسخه‌ی سوم فرنچایز رسید و مدتی قبل نیز شرکت کپکام به طور رسمی از بازسازی کامل بازی پرطرفدار Resident Evil 4 رونمایی کرد.\r\n\r\nبا این حال، با ورود کنسول‌های جدید ایکس‌باکس و پلی‌استیشن به بازار، این شرکت ناشر ژاپنی چند ماه پیش در پیامی اعلام کرد که سه بازی Resident Evil 2 و Resident Evil 3 و Resident Evil 7 در اواخر سال ۲۰۲۲ میلادی با کیفیت بهتر و به همراه ویژگی‌های جدید از جمله پشتیبانی از تکنولوژی رهگیری پرتو یا همان ری‌تریسینگ برای کنسول‌های ایکس باکس سری ایکس، ایکس باکس سری اس و پلی‌استیشن 5 و همچنین پلتفرم رایانه‌ی شخصی منتشر می‌شوند.\r\n\r\nبر این اساس، ساعاتی پیش تیم توسعه‌دهنده خاطرنشان کرد که به خاطر بهبودهای گرافیکی، حداقل سیستم مورد نیاز بازی رزیدنت اویل ۲ در پلتفرم رایانه‌ی شخصی تغییر کرده است. به بیان دقیق‌تر، در حالی که این عنوان پیش‌تر روی ویندوزهای ۷ و ۸.۱ قابل اجرا بود، لیکن بعد از دریافت به‌روزرسان ارتقا به نسل نهم، صرفا روی ویندوز ۱۰ یا ۱۱ اجرا خواهد شد. از طرفی، در نسخه‌ی قبلی، پشتیبانی سخت‌افزارها از نسخه‌ی یازدهم دایرکت ایکس الزامی بود که اکنون باید از نسخه‌ی دوازدهم دایرکت ایکس استفاده کنید.\r\n\r\nماجرا صرفا به این موارد خلاصه نشده و در حالی که بازیکنان با استفاده از کارت گرافیک‌های انویدیا GeForce GTX 760 و ای‌ام‌دی Radeon R7 260X قادر به تجربه‌ی بازی بودند، اما از این پس به کارت گرافیک‌های انویدیا جی‌فورس GTX 960 و AMD Radeon RX 460 با حافظه‌ی اختصاصی دو گیگابایت احتیاج خواهند داشت. گفتنی است که مشخصات پردازنده مرکزی و فضای حافظه رم نسبت به گذشته تغییری نکرده است.\r\n\r\n\r\nدر ادامه می‌توانید مشخصات کامل سیستم مورد نیاز جدید بازی رزیدنت اویل ۲ را مشاهده کنید:\r\nحداقل سیستم مورد نیاز Resident Evil 2\r\n\r\n    سیستم عامل: ویندوز ۱۰ (فقط ۶۴ بیتی)\r\n    پردازنده مرکزی اینتل: Intel Core i5 4460 با فرکانس ۳.۲ گیگاهرتزی\r\n    پردازنده مرکزی ای‌ام‌دی: AMD FX 6300 با فرکانس ۳.۵ گیگاهرتزی\r\n    حافظه‌ی رم: ۸ گیگابایت\r\n    پردازنده گرافیکی انویدیا: Nvidia GeForce GTX 960\r\n    پردازنده گرافیکی ای‌ام‌دی: AMD Radeon RX 460\r\n    حافظه پردازنده گرافیکی: ۲ گیگابایت\r\n    دایرکت ایکس: نسخه ۱۲\r\n    فضای ذخیره‌سازی: ۲۶ گیگابایت\r\n\r\nلازم به ذکر است که بازی رزیدنت اویل ۲ در حال حاضر برای پلتفرم‌های ایکس‌باکس وان و پلی‌استیشن 4 و پی‌سی در دسترس قرار دارد. همچنین، به لطف پشتیبانی کنسول‌های جدید دو شرکت مایکروسافت و سونی از بازی‌های نسل قبلی، دارندگان ایکس باکس سری ایکس، ایکس باکس سری اس و پلی‌استیشن 5 نیز قادر به تجربه‌ی این عنوان هستند که البته کیفیت بصری و گرافیک فنی آن مشابه با نسل هشتم خواهد بود.\r\n\r\nبا این حال، در زمان انتشار نسخه‌ی بهبود یافته، انتظار می‌رود که بازی با نرخ فریم و رزولوشن بالاتر نسبت به گذشته اجرا شود. به علاوه، احتمالا این عنوان از ویژگی رهگیری پرتو، کاهش چشمگیر سرعت لودینگ، صدای سه بعدی و بازخورد لمسی برای کنترلر دوال سنس پشتیبانی خواهد کرد. ناگفته نماند که نسخه‌ی نسل نهم بازی Resident Evil 2 هنوز تاریخ دقیقی برای انتشار ندارد و صرفا شرکت کپکام از عرضه‌ی آن در اواخر سال ۲۰۲۲ میلادی خبر داده بود. ذکر این نکته مهم است که دارندگان بازی به رایگان به‌روزرسان ارتقا به نسل نهم را دریافت می‌کنند.', 2, 1, '1', 58, '2022-05-21 21:58:54'),
(10, 'داستان فیلم Gran Turismo بسیار هیجان‌انگیز خواهد بود!', '/Techblog/assets/upload/images/article/20220707215854.jpg', 'طبق آخرین گزارش‌ها، ظاهرا داستان فیلم Gran Turismo برخلاف جهان بازی این سری معروف، دارای یک روایت هیجان‌انگیز خواهد بود.\r\n\r\nسال‌هاست که ما شاهد عرضه عناوین مختلف سری Gran Turismo هستیم؛ بازی‌هایی که صرفا درباره مسابقات اتوموبیل‌رانی هستند و انواع و اقسام ماشین‌های روز را مشاهده خواهید کرد. در واقع عنوان اخیر Gran Turismo 7 دقیقا برای چندمین بار همین سنت را تکرار کرد و باز هم شاهد یک بازی فوق گسترده از اضافه شدن مدل‌های مختلف ماشین‌های قدیمی و جدید بودیم. با این حال، همیشه یک نقطه ضعف، یا شاید بهتر است بگوییم جای خالی در این سری وجود داشته که هیچوقت هم طی این سال‌ها پر نشد.\r\n\r\nدر واقع بازی‌های سری GT شبیه‌سازهای درجه ۱ و بی‌نظیری برای رانندگی بوده‌اند و حجت را هم بر این مورد تمام کرده‌اند؛ اما نکته قابل توجه اینجاست که ما هرگز شاهد داستان خاصی از آن نبوده‌ایم! در حقیقت حدود چند هفته پیش بود که شنیدیم سونی (Sony) قصد دارد تا بازی‌های مختلف انحصاری پلی استیشن را در قالب فیلم و سریال به تصویر بکشد. این در حالی است که سریال The Last of Us همین الآن هم ساخته شده و از طرفی برای عناوین God of War یا حتی Astro’s Playroom هم می‌توان داستان خاصی را در نظر گرفت. اما هنگامی که صحبت از سری Gran Turismo باشد، قاعدتا گزینه خاصی برای روایت یک داستان وجود نخواهد داشت!\r\n\r\nبه هر حال رسالت سری بازی‌های GT این بوده که یک شبیه‌ساز اتوموبیل‌رانی با سطح گرافیک و طراحی عالی را ارائه کنند و از طرفی انتظاری هم نمی‌رود که برای آن‌ها داستان در نظر گرفته شود. اما وقتی قرار است تا یک فیلم از Gran Turismo ساخته شود، قاعدتا نیاز است تا یک روایت خاص برای آن نوشته شده باشد؛ مگر اینکه سازندگان بخواهند یک مونتاژ ۲:۳۰ ساعته از مسابقه دادن راننده‌های معروف را به عنوان فیلم سینمایی عرضه کنند!\r\n\r\nبا این وجود جالب است بدانید که برای انتخاب یک روش خوب برای روایت داستان فیلم Gran Turismo اخیرا اعلام شده که ظاهرا سازندگان قصد دارند تا از بخش GT Academy الهام بگیرند. در واقع از سال ۲۰۰۸ استودیو پولیفونی دیجیتال (Polyphony Digital) با همکاری نیسان (Nissan) و همچنین سونی موفق شد تا یک آکادمی رانندگی به نام GT Academy را بنیان‌گذاری کرده و در آن بازیکنان را به رقابتی جدی دعوت کند؛ جایی که افراد پس از برنده شدن به مسابقات واقعی با ماشین‌های واقعی دعوت می‌شوند و در صورت برنده شدن در آن، به کلی مسیر زندگیشان عوض می‌شود!\r\n\r\n\r\n\r\nدر این بخش بازیکنان عادی بازی‌های ویدیویی عملا این شانس را پیدا می‌کنند تا به یک راننده واقعی و حرفه‌ای تبدیل شوند. حال یکبار دیگر همین جمله را بخوانید؛ ایده داستان فیلم Gran Turismo پیدا شد! در واقع طبق گزارش‌های اخیر سازندگان قصد دارند تا از همین ایده به عنوان پایه داستان استفاده کرده و با ایجاد تغییراتی آن را هیجان‌انگیزتر کنند.\r\n\r\nنیل بلوکمپ (Neil Blomkamp)، کارگردان آثاری همچون District 9 و Elysium ساخت فیلم Gran Turismo را بر عهده خواهد داشت، و از طرفی سعی دارد تا یک داستان براساس واقعیت را به تصویر بکشد؛ جایی که یک گیمر حرفه‌ای و با استعداد، در مسابقات GT انقدر برنده می‌شود تا روزی خود به آکادمی مسابقات واقعی دعوت شده و در آن‌ها به یک راننده حرفه‌ای تبدیل می‌شود.\r\n\r\nدر واقع سازندگان نام کسی که قرار است تا داستان را درباره آن روایت کنند نبرده‌اند، اما قاعدتا نظر آن‌ها روی لوکاس اوردونز (Lucas Ordonez) خواهد بود؛ کسی که در GT Academy و در مسابقات مجازی چندین بار برنده شد و نهایتا با راه پیدا کردن به مسابقات با ماشین‌های واقعی، توانست ثابت کند که می‌تواند قهرمانی خود را خارج از جهان بازی‌ها هم جشن بگیرد. او پس از این موفقیت به زندگی به عنوان یک راننده حرفه‌‌ای ادامه داد و حتی در مسابقات کاپ GT4 برنده شد و چندین موفقیت دیگر را هم به عنوان راننده به رزومه خود اضافه کرد. لازم به ذکر است که همکاری اودونز با کمپانی نیسان همچنان ادامه دارد و از طرفی نویسندگان داستان فیلم Gran Turismo احتمالا با او مصاحبه خواهند کرد.\r\n\r\nفیلم سینمایی Gran Turismo در تاریخ ۲۰ مرداد ۱۴۰۲ (۱۱ آگوست ۲۰۲۳) اکران خواهد شد.', 1, 5, '1', 144, '2022-05-21 21:58:54'),
(11, 'نمایش سرزمین میانه در تریلر جدید سریال ارباب حلقه ها', '/Techblog/assets/upload/images/article/20220826180056.jpg', 'طبق جدیدترین اخبار سینما و تلویزیون، تریلر جدیدی از سریال The Lord of the Rings: The Rings of Power منتشر شده است. آمازون که تاکنون تریلرهای متعددی از سریال ارباب حلقه ها منتشر کرده، امروز طبق وعده‌اش تریلر جدید و رسمی سریال را منتشر کرده است که بخش زیادی از تصاویر آن را در تریلرهای گذشته سریال دیده بودیم و صد البته حاوی تصاویر جدیدی نیز است. در ادامه می‌توانید این تریلر را تماشا کنید:\r\n\r\n\r\nمورفید کلارک، رابرت آرمایو، مارکلا کاونا، جوزف ماورل، اما هوروت، اووین آرتور، نازنین بنیادی، اسماعیل کروز کوردووا، تایرو موهافیدین، سوفیا نامویت، مگان ریچاردز، دیلان اسمیت، چارلی ویکرز، سینتیا آدای-رابینسون، مکسیم بالدری، ایان بلکبرن، کیپ چپمن، آنتونی کرام، مکسین کانلیف، تریستان گراول، سر لنی هنری، توسیتا جِیاساندرا، فابین مک‌کالوم، سایمن مرلز، جف مورِل، پیتر مولان، لوید اووِن، آگوستوس پرو، پیتر تِیت، الکس تارانت، لئون وادام، بنجامین واکر، سارا زوانگوبانی، چارلز ادواردز، ویل فلچر، آملی چایلد-ویلیرز و بو کسیدی، از جمله بازیگرانی هستند که در سریال The Lord of the Rings: The Rings of Power حضور دارند و به ایفای نقش پرداخته‌اند.\r\n\r\n\r\nاتفاقات سریال The Lord of the Rings: The Rings of Power قرار است در دوره دوم جریان داشته باشد. هزینه ساخت فصل اول سریال ارباب حلقه‌ها حدود ۴۶۵ میلیون دلار بوده است و این سریال قرار است در ۵ فصل ساخته و از امازون پخش شود. جان دی. پین و پاتریک مک‌ کی به‌عنوان شورانر، نویسنده و تهیه‌کننده اجرایی در سریال ارباب حلقه‌ها حضور دارند. همچنین خوان آنتونیو بایونا، فوین چه ییپ و شارلوت برنداستروم، وظیفه کارگردانی قسمت‌های مختلف فصل اول سریال The Lord of The Rings را بر عهده داشته‌اند.\r\n\r\n\r\n\r\nپخش سریال The Lord of the Rings: The Rings of Power که در ۸ قسمت تولید شده، از روز پنج‌شنبه ۱ سپتامبر (۱۰ شهریور) آغاز خواهد شد که به‌جای یک قسمت، اینبار شاهد پخش همزمان دو قسمت اول این سریال خواهیم بود. البته ساعت پخش آن به وقت ایران بامداد روز جمعه خواهد بود. همچنین دو قسمت اول سریال ارباب حلقه‌ها قرار است یک روز قبل شروع پخش در چند سال سینما نمایش داده شود. سریال The Lord of the Rings هم‌اکنون برای فصل دوم تمدید شده است.', 6, 1, '1', 6, '2022-08-02 18:15:00'),
(12, 'سریال خاندان اژدها در اروپا برای HBO و HBO Max رکوردشکنی کرد', '/Techblog/assets/upload/images/article/20220826175907.jpg', 'سریال خاندان اژدها، پیش‌درآمد سریال Game of Thrones با پخش اولین قسمت توانست افتتاحیه‌ای بزرگ در کشورهای اروپایی را تجربه کند. در حقیقت رکورد «بیشترین مخاطب سریال یا فیلم جدید در شبکه آنلاین متعلق به HBO در اروپا» به نام سریال House of the Dragon ثبت شد.\r\n\r\nاین اطلاعات تازه در حالی به رسانه‌ها رسید که براساس جدیدترین اخبار سینما و تلویزیون می‌دانیم در زمان پخش، ۹/۹۸۶ میلیون بیننده‌ی آمریکایی شبکه HBO و HBO Max قسمت اول سریال House of the Dragon را تماشا کردند؛ عددی که بزرگ‌تر از تعداد بینندگان قسمت اول هر سریال دیگر از HBO در آمریکا تا امروز است.\r\n\r\n\r\n\r\nمحصول تلویزیونی در اروپا نوشت: «پلتفرم [اچ‌بی‌او] از ساعات آغازین دوشنبه با تقضای بی‌سابقه مواجه شد که در عصر آن روز اوج گرفت». با اینکه آمار دقیق تعداد بینندگان نخستین قسمت سریال خاندان اژدها در کشورهای اروپایی را نداریم، نماینده‌ی شبکه آنلاین HBO Max می‌گوید: «خاندان اژدها با اختلاف بزرگ‌ترین افتتاحیه‌ی هر اثر در تاریخ برای HBO و HBO Max در اروپا را دارد. تعداد تماشاگرهای قسمت اول از همه انتظارات فراتر رفت».\r\n\r\nشرکت Comcast که وظیفه‌ی پخش سریال House of the Dragon در بریتانیا ازطریق شبکه‌های Sky را دارد، اعلام کرد که قسمت نخست خاندان اژدها در انگلستان با ۱.۹ میلیون بیننده توانست رکورد ثبت‌شده توسط قسمت اول سریال بازی تاج و تخت و هر سریال آمریکایی دیگر پخش‌شده از Sky تا امروز را بشکند.\r\n\r\nداستان‌گویی سریال خاندان اژدها از ۱۷۳ سال قبل از تولد دنریس تارگرین شروع می‌شود؛ نکته‌ای که به خوبی به مخاطب‌های سریال Game of Thrones نشان می‌دهد که این اثر تازه آن‌ها را به کدام بخش از تاریخ وستروس می‌برد. مت اسمیت در نقش دیمون تارگرین و میلی الکاک در نقش رینیرا تارگرین از بازیگرهای قسمت نخست سریال House of the Dragon هستند. فصل اول خاندان اژدها در مجموع ۱۰ اپیزود دارد.', 6, 2, '1', 12, '2022-08-07 18:28:52'),
(13, 'توسعه The Lords of the Fallen روی آنریل انجین 5', '/Techblog/assets/upload/images/article/20220826174937.jpg', 'در روزهای گذشته و در جریان رویداد افتتاحیه‌ی مراسم گیمزکام امسال، ریبوت سری Lords of the Fallen توسط استودیوی سازنده‌ی Hexworks و شرکت ناشر CI Games معرفی شد. این اثر بازآفرینی‌شده، The Lords of the Fallen نام دارد و روی آنریل انجین 5 توسعه می‌یابد. داستان آن هزار سال پس از وقایع نسخه‌ی اصلی جریان دارند و به مانند Lords of the Fallen، یک بازی اکشن نقش‌آفرینی سولزلایک به شمار می‌آید.\r\n\r\nحال ناشر بازی The Lords of the Fallen در یک بیانیه‌ی مطبوعاتی جزئیات بیشتری را در مورد آن بیان کرده است. شرکت CI Games اشاره کرد که دنیای به هم پیوسته‌ی این بازی پنج برابر بزرگتر از نسخه‌ی اصلی است. همچنین درکنار مأموریت‌های مربوط‌به NPCها، شخصیت‌های پرداخته‌شده و روایت غنی، بازیکنان می‌توانند شخصیت خاص خود را خلق کنند.\r\n\r\nیک ویژگی جدید که تیم Hexworks برای ریبوت Lords of the Fallen در نظر دارد، پشتیبانی آنلاین از دو بازیکن در حالت کوآپ است که سازندگان قول داده‌اند این ویژگی بدون وقفه در اختیار بازیکنان باشد. در مورد تیم توسعه می‌توان گفت که استودیوی Hexworks شامل بیش از ۷۵ نفر در تیم توسعه‌ی داخلی و شبکه‌ای از همکاران خارجی است.\r\n\r\nهم‌اکنون کارگردانی خلاقیت بازی The Lords of the Fallen به عهده‌ی سزار ویرتوسو است که سابقه‌ی کار روی ساخت Shadow of the Tomb Raider و Assassin’s Creed Origins را دارد. همچنین سائول گاسکن، تهیه کننده‌ی اجرایی این بازی خواهد بود که قبلا در ساخت بازی‌هایی همچون Dead by Daylight و PayDay 2 مشارکت داشته است.\r\n\r\nویرتوسو در مصاحبه‌ی جدید اظهار داشت: «بازی The Lords of the Fallen دنباله‌ی معنوی نسخه‌ی اصلی است که طرفداران همیشه آن را درخواست می‌کردند. این بازی به‌طور قابل توجهی بزرگتر است، با یک جهان دوگانه‌ی گسترده و به هم پیوسته که بین قلمرو زنده‌ها و مردگان تقسیم شده است و بازیکنان می‌توانند به‌تنهایی یا با دوستان خود آن را کشف کنند. این بازی تاریک‌تر و چالش‌برانگیزتر است و نبردهای سریع‌تر شبیه به سری سولز، موضوعی قوی‌تر، روایت غنی‌تر، سیستم‌های نقش‌آفرینی عمیق‌تر و داستان‌گویی همه‌جانبه‌تری را نسبت به نسخه‌ی اصلی خواهد داشت».\r\n\r\nعلاوه‌بر این، کارگردان خلاقیت بازی The Lords of the Fallen اعلام کرد که بازیکنان می‌توانند در آن از جادویی ویرانگر استفاده کنند. با اینکه شرکت CI گیمز هنوز تاریخ انتشار دقیق The Lords of the Fallen را مشخص نکرده، اما این بازی قرار است در سال ۲۰۲۳ میلادی روی پلتفرم پی سی و کنسول‌های پلی استیشن 5 و ایکس باکس سری ایکس | ایکس باکس سری اس عرضه شود. با شروع کمپین بازاریابی جهانی در سال جاری، انتظار می‌رود در ماه‌های آینده شاهد نمایش‌های بیشتری از گیم‌پلی بازی باشیم.', 3, 3, '1', 37, '2022-08-16 20:22:20'),
(14, 'وجود مرحله انحصاری در نسخه پلی استیشن بازی Hogwarts Legacy', '/Techblog/assets/upload/images/article/20220826174831.jpg', 'در چند روز گذشته بازی Hogwarts Legacy پس از ماه‌ها سکوت خبری دوباره بر سر زبان‌ها افتاد و بخش‌های بیشتری از این اثر اکشن نقش‌آفرینی جهان-باز که استودیو آوالانچ سافتور روی آن کار می‌کند، به نمایش گذاشته شد. اخیرا امکان پیش‌خرید نسخه‌های مختلف بازی فراهم شد و جزئیات جالبی از این محصول را در اختیار بازیکن‌ها قرار داد.\r\n\r\nصفحه‌ی بازی Hogwarts Legacy در فروشگاه پلی استیشن به وجود یک مأموریت انحصاری اشاره کرده است که تنها در دسترس کاربران پلی استیشن قرار می‌گیرد. ماهیت دقیق این مأموریت و جزئیاتی نظیر مدت زمان مورد نیاز برای تکمیل آن مشخص نیست و شرکت WB Games در این‌باره سکوت اختیار کرده است.\r\n\r\nبااین‌حال چندلر وود، مدیر بازاریابی استودیو Avalanche Software اخیرا با انتشار یک توئیت تازه توضیح داد که این مأموریت انحصاری در اختیار همه‌ی خریداران نسخه‌های پلی استیشن 4 و پلی استیشن 5 بازی Hogwarts Legacy قرار می‌گیرد و هیچ ارتباطی با پیش خرید آن ندارد. او در این‌باره گفت: «این مأموریت به پیش‌خرید بازی ارتباطی ندارد. با پیش‌خرید نسخه‌ی پلی استیشن می‌توان دستور تهیه‌ی معجون Felix Felicis را دریافت کرد».\r\n\r\nبازی Hogwarts Legacy یکی از بزرگ‌ترین نمایش‌های خود را در برنامه State of Play‌ شرکت سونی داشت. پس ظاهرا برای تبلیغ و عرضه‌ی هاگوارتز لگسی، پلی استیشن و شرکت برادران وارنر ارتباطی قابل‌توجه با یکدیگر داشته‌اند. در هر حالت این بازی که ما را به مدرسه علوم و فنون جادویی هاگوارتز می‌برد، در تاریخ ۱۰ فوریه ۲۰۲۳ (۲۱ بهمن ۱۴۰۱) برای پلتفرم‌های پلی استیشن 5، پلی استیشن 4، ایکس باکس سری ایکس | ایکس باکس سری اس، ایکس باکس وان و کامپیوتر عرضه خواهد شد. سپس بازی در زمان نامعلوم دیگری به کنسول نینتندو سوییچ نیز راه خواهد یافت.', 3, 5, '1', 50, '2022-08-25 16:02:57'),
(15, 'وجود قابلیت های فراوان در The Last of Us Part 1 برای گیمرهای دار', '/Techblog/assets/upload/images/article/20220828173549.jpg', 'ناتی‌داگ که به‌عنوان یکی از استودیوهای پیشتاز در زمینه Accessibility شناخته می‌شود، کاری کرده است که بیشترین تعداد ممکن از گیمرها بتوانند از The Last of Us Part 1 لذت ببرند.\r\n\r\nبازی The Last of Us Part I (ریمیک لست آو آس) در زمینه Accessibility (در دسترس بودن بیشتر ویدیوگیم برای بازیکنان محترم دارای شرایط جسمی یا ذهنی خاص) پا را از بازی The Last of Us Part II هم فراتر می‌گذارد. استودیوی بازی‌سازی ناتی‌داگ و بخش سرگرمی‌های تعاملی شرکت سونی با پخش یک تریلر تازه نشان دادند که چه‌قدر در بازسازی The Last of Us به موضوع مهم دسترسی‌پذیر بودن بازی توجه شده است.\r\n\r\n\r\n\r\nخوش‌بختانه طی سال‌های اخیر، Accessibility به مراتب بیشتر از قبل مورد توجه قرار گرفت. در این بین بازی The Last of Us Part II یکی از آثاری بود که تاثیر قابل توجهی روی اهمیت دادن بیشتر بسیاری از ناشرها و استودیوها به Accessibility داشت. چرا که با استفاده از گزینه‌های شخصی‌سازی مختلف اجازه داد که تعداد قابل توجهی از بازیکن‌ها که به خاطر شرایط خود و برخی از محدودیت‌ها معمولا نمی‌توانند از بسیاری از بازی‌ها لذت ببرند، فرصت تجربه‌ی کامل TLoU Part 2 را داشته باشند.\r\n\r\n\r\nدر جدیدترین اخبار بازی مشخص شد که بازی The Last of Us Part 1 کنسول پلی استیشن 5 نه‌تنها همه‌ی قابلیت‌های Accessibility موجود در بازی The Last of Us Part 2 را دارد، بلکه امکانات بیشتری را نیز به آن‌ها اضافه کرده است. یکی از امکانات، توصیف صوتی همه کات‌سین‌ها است که کمک زیادی به گیمرهای دارای مشکلات بینایی خواهد کرد. یکی دیگر از قابلیت‌های خاص Accessibility بازی که واقعا می‌توان آن را نوآوری ناتی‌داگ در این زمینه دانست، مربوط‌به استفاده از قابلیت هپتیک فیدبک (لرزش‌های دقیق و پرجزئیات) دسته دوال‌سنس برای انتقال کامل حس‌وحال بیان شدن جملات است.\r\n\r\nبه عبارت بهتر گیمرهای ناشنوا و کم‌شنوا که در اصل باید با تمرکز روی زیرنویس و نگاه کردن به بازی در جریان داستان‌گویی آن قرار بگیرند، به کمک لرزش دقیق دسته PS5 متوجه می‌شوند که هر کلمه در هر بخشی از دیالوگ‌ها با چه میزان از انرژی و تاکید توسط بازیگر بیان شده است. مثلا این‌گونه خشم، بیان مهربانانه و زمزمه کردن یک نفر در گوش شخص دیگر که همگی روی بار احساسی لحظات مختلف بازی تاثیر می‌گذارند، همه و همه با لرزش به‌دست‌های گیمر منتقل می‌شوند تا بدون شنیدن بتواند فضاسازی احساسی اثر در هر بخش را بهتر درک کند.\r\n\r\nحالت‌های تصویری مناسب برای افراد دارای شرایط مختلف بینایی همچون کوررنگی، گزینه‌های مختلف برای شخصی‌سازی درجه سختی و همین‌طور امکانات گوناگون برای افرادی که برخی از عضله‌های آن‌ها عملکرد ایده‌آلی را از خود نشان نمی‌دهند، از دیگر قابلیت‌های Accessibility ریمیک لست آو آس به شمار می‌آیند.\r\n\r\nبازی The Last of Us Part I در روز جمعه همین هفته روی کنسول پلی استیشن 5 سونی تقدیم گیمرها می‌شود. این اثر در آینده برای پلتفرم PC هم عرضه خواهد شد.', 3, 1, '1', 20, '2022-08-28 17:35:49'),
(16, 'اطلاعات جدیدی از بازی Assassin\'s Creed Mirage فاش شد', '/Techblog/assets/upload/images/article/20220904181457.jpg', 'در مدت یک هفته مانده به رونمایی رسمی از Assassin\'s Creed Mirage، جزئیات جدیدی از این بازی فاش شده است.\r\n\r\nچند روز قبل، یوبی‌سافت نام جدیدترین بازی از مجموعه‌ی Assassin\'s Creed را تحت عنوان Mirage اعلام کرد. این شرکت فرانسوی در بیانیه‌ی خود گفت که رونمایی کامل بازی در تاریخ ۱۹ شهریور صورت خواهد گرفت.\r\n\r\nحالا براساس اطلاعات منتشر شده توسط یکی از لیکرهای مشهور سری Assassin’s Creed، داستان بازی تنها در شهر بغداد جریان خواهد داشت. این شهر در بازی شامل چهار منطقه‌ی مختلف است و هر منطقه دارای یک باس خواهد بود.\r\n\r\nپیش از این در گزارشی اعلام شده بود که Mirage به ریشه‌های مجموعه‌ی Assassin’s Creed باز خواهد گشت. در این بازی دوباره شاهد بازگشت پرتاب چاقو، مکان‌های مخصوص مخفی‌کاری روی سقف ساختمان‌ها و امکان مخفی کاری با استفاده از پنهان شدن در میان جمعیت NPCهای بازی خواهیم بود. همچنین به نظر می‌رسد که روی پارکور بازی بیشتر کار شده است.\r\n\r\nمطابق گزارش‌ها، سازندگان قصد دارند تا تراکم جمعیت Mirage را مشابه بازی Assassin’s Creed Unity بازسازی کند. در Unity همواره شاهد حضور تعداد زیادی از NPC‌های بازی به‌صورت یکجا در صفحه بودیم.\r\n\r\nبرای اولین‌بار، رسانه‌ی بلومبرگ در گزارشی اعلام کرد که یوبی‌سافت قصد دارد Assassin’s Creed جدید را با تمرکز بر مخفی کاری و وفاداری بیشتر به ریشه‌های مجموعه عرضه کند؛ یعنی احتمالا Mirage از حال و هوای سه نسخه‌ی اخیر که بیشتر بر پایه‌ی المان‌های نقش آفرینی بودند، فاصله خواهد گرفت.\r\n\r\nبازی Assassin’s Creed Mirage، توسط شعبه‌ی بوردو یوبی‌سافت در دست توسعه قرار دارد. براساس گزارش بلومبرگ این بازی، بهار سال ۲۰۲۳ دردسترس علاقه‌مندان قرار خواهد گرفت. بااین‌حال باید تا ۱۹ شهریور منتظر انتشار اخبار رسمی درمورد این بازی موردانتظار ماند.', 3, 4, '1', 134, '2022-09-03 21:08:57');

